&lt;svg id="mindmap"&gt;&lt;/svg&gt;
&lt;script src="https://cdn.jsdelivr.net/npm/d3@7.9.0/dist/d3.min.js"&gt;&lt;/script&gt;&lt;script src="https://cdn.jsdelivr.net/npm/markmap-view@0.18.10/dist/browser/index.js"&gt;&lt;/script&gt;&lt;script src="https://cdn.jsdelivr.net/npm/markmap-toolbar@0.18.10/dist/index.js"&gt;&lt;/script&gt;&lt;script&gt;(()=&gt;{setTimeout(()=&gt;{const{markmap:x,mm:K}=window,P=new x.Toolbar;P.attach(K);const F=P.render();F.setAttribute("style","position:absolute;bottom:20px;right:20px"),document.body.append(F)})})()&lt;/script&gt;&lt;script&gt;((b,L,T,D)=&gt;{const H=b();window.mm=H.Markmap.create("svg#mindmap",(L||H.deriveOptions)(D),T)})(()=&gt;window.markmap,null,{"content":"Variables","children":[{"content":"Definici&#xf3;n de Variable","children":[{"content":"Caracter&#xed;stica que se observa o mide sobre las unidades estad&#xed;sticas","children":[],"payload":{"tag":"li","lines":"8,9"}},{"content":"Var&#xed;a entre individuos de la poblaci&#xf3;n o muestra","children":[],"payload":{"tag":"li","lines":"9,10"}},{"content":"Representa atributos o propiedades medibles, observables o categorizables","children":[],"payload":{"tag":"li","lines":"10,13"}}],"payload":{"tag":"h2","lines":"7,8"}},{"content":"Tipos de Variables","children":[{"content":"Variables Cuantitativas","children":[{"content":"Cuantitativas Discretas","children":[{"content":"No aceptan valores decimales (conteo)","children":[],"payload":{"tag":"li","lines":"21,22"}},{"content":"Resultan de procesos de enumeraci&#xf3;n","children":[],"payload":{"tag":"li","lines":"22,23"}},{"content":"Ejemplos: n&#xfa;mero plantas, racimos por planta, ausencias por mes","children":[],"payload":{"tag":"li","lines":"23,25"}}],"payload":{"tag":"h4","lines":"20,21"}},{"content":"Cuantitativas Continuas","children":[{"content":"Pueden asumir cualquier valor dentro de un rango, incluyendo decimales","children":[],"payload":{"tag":"li","lines":"26,27"}},{"content":"Resultan de procesos de medici&#xf3;n","children":[],"payload":{"tag":"li","lines":"27,28"}},{"content":"Ejemplos: altura de plantas, peso de semillas, temperatura","children":[],"payload":{"tag":"li","lines":"28,30"}}],"payload":{"tag":"h4","lines":"25,26"}}],"payload":{"tag":"h3","lines":"16,17"}},{"content":"Variables Cualitativas","children":[{"content":"Cualitativas Dicot&#xf3;micas","children":[{"content":"Dos modalidades","children":[],"payload":{"tag":"li","lines":"35,36"}},{"content":"Ejemplos: sexo (masculino/femenino), resultado (aprobado/reprobado)","children":[],"payload":{"tag":"li","lines":"36,38"}}],"payload":{"tag":"h4","lines":"34,35"}},{"content":"Cualitativas Polit&#xf3;micas","children":[{"content":"M&#xe1;s de dos categor&#xed;as","children":[],"payload":{"tag":"li","lines":"39,40"}},{"content":"Ejemplos: estado civil, color de ojos, lugar de origen, susceptibilidad a enfermedades","children":[],"payload":{"tag":"li","lines":"40,41"}}],"payload":{"tag":"h4","lines":"38,39"}}],"payload":{"tag":"h3","lines":"30,31"}}],"payload":{"tag":"h2","lines":"13,14"}}],"payload":{"tag":"h1","lines":"5,6"}},{"maxWidth":300})&lt;/script&gt;
